import React, { useState } from "react";
import { registration } from "../../action/user";
import Input from "../input/Input";
import './authorization.css'

const Registartion = () =>{
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [repeatPassword, setRepeatPassword] = useState("")
    return(
        <div className="registration">
            <div className="rectangle">
                <div className="gray_rectangle">
                    <div className="circle"></div>
                    <div className="circle2"></div>
                    </div>
            <div className="">email</div>
            <Input value={email} setValue = {setEmail} type="text" className="password_input" />

            <div className="">password</div>
            <Input value={password} setValue = {setPassword} type="password" className="password_input"/>

            <div className="">repeat password</div>
            <Input type="password" value={repeatPassword} setValue = {setRepeatPassword} />

            <button className="sinup" onClick={()=>{
                if(password !== repeatPassword){
                    alert("pass are not the same")
                }else{
                    registration(email, password)
                }
            }}>Sing up</button>
        </div>
        </div>
    )

}

export default Registartion;
